import { ActivatedRoute, Router } from '@angular/router';
import { FormBuilder, Validators } from '@angular/forms';

import { AccountService } from '../services/account.service';
import { Component } from '@angular/core';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css'],
})
export class RegisterComponent {
  form = this.formBuilder.group({
    firstName: ['', Validators.required],
    lastName: ['', Validators.required],
    email: ['', Validators.required],
    password: ['', Validators.required],
    genre: ['', Validators.required],
    admin: ['', Validators.required]
  });
  loading = false;
  submitted = false;

  constructor(
    private formBuilder: FormBuilder,
    private route: ActivatedRoute,
    private router: Router,
    private accountService: AccountService
  ) {
    this.form.valueChanges.subscribe((value) => console.log(value));

    this.f.password.valueChanges.subscribe((value) => console.log(value));
  }

  // convenience getter for easy access to form fields
  get f() {
    return this.form.controls;
  }

  onSubmit() {
    this.submitted = true;

    // stop here if form is invalid
    if (this.form.invalid) {
      return;
    }

    this.loading = true;
    this.accountService.register(this.form.value as any).subscribe({
      next: () => {
        this.router.navigate(['/login'], { relativeTo: this.route });
      },
      error: (error) => {
        this.loading = false;
      },
    });
  }
}
<div class="card">
  <h4 class="card-header">Register</h4>
  <div class="card-body">
    <form [formGroup]="form" (submit)="onSubmit()">
      <div class="form-group">
        <label for="firstName">First Name</label>
        <input
          type="text"
          formControlName="firstName"
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f.firstName.errors }"
        />
        <div *ngIf="submitted && f.firstName.errors" class="invalid-feedback">
          <div *ngIf="f.firstName.errors['required']">First Name is required</div>
        </div>
      </div>
      <div class="form-group">
        <label for="lastName">Last Name</label>
        <input
          type="text"
          formControlName="lastName"
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f.lastName.errors }"
        />
        <div *ngIf="submitted && f.lastName.errors" class="invalid-feedback">
          <div *ngIf="f.lastName.errors['required']">Last Name is required</div>
        </div>
      </div>
      <div class="form-group">
        <label for="email">Email</label>
        <input
          type="text"
          formControlName="email"
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f.email.errors }"
        />
        <div *ngIf="submitted && f.email.errors" class="invalid-feedback">
          <div *ngIf="f.email.errors['required']">Email is required</div>
        </div>
      </div>
      <div class="form-group">
        <label for="password">Password</label>
        <input
          type="password"
          formControlName="password"
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f.password.errors }"
        />
        <div *ngIf="submitted && f.password.errors" class="invalid-feedback">
          <div *ngIf="f.password.errors['required']">Password is required</div>
          <div *ngIf="f.password.errors['minlength']">
            Password is not valid (length should have at least 6 characters)
          </div>
        </div>
      </div>
      <div class="form-group">
        <label for="genre">Genre</label>
        <input
          type="text"
          formControlName="genre"
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f.genre.errors }"
        />
        <div *ngIf="submitted && f.genre.errors" class="invalid-feedback">
          <div *ngIf="f.genre.errors['required']">Genre is required</div>
        </div>
      </div>
      <div class="form-group">
        <label for="admin">Admin</label>
        <input
          type="text"
          formControlName="admin"
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f.admin.errors }"
        />
        <div *ngIf="submitted && f.admin.errors" class="invalid-feedback">
          <div *ngIf="f.admin.errors['required']">Admin is required</div>
        </div>
      </div>
      <div class="form-group">
        <button class="btn btn-primary" type="submit">
          <span
            *ngIf="loading"
            class="spinner-border spinner-border-sm mr-1"
          ></span>
          Register
        </button>
        <a routerLink="../login" class="btn btn-link">Cancel</a>
      </div>
    </form>
  </div>
</div>
